	styleInject(`harmony-context-menu{background-color:green;background-color:var(--theme-context-menu-bg-color);cursor:not-allowed;font-size:1.5em;overflow:auto;position:absolute;z-index:100000}.harmony-context-menu-item{background-color:green;background-color:var(--theme-context-menu-item-bg-color);cursor:pointer}.harmony-context-menu-item.disabled{pointer-events:none}.harmony-context-menu-item.selected{background-color:blue;background-color:var(--theme-context-menu-item-selected-bg-color)}.harmony-context-menu-item.separator{background-color:#000;height:5px}.harmony-context-menu-item>.harmony-context-menu-item-title:hover{background-color:var(--theme-context-menu-item-hover-bg-color)}.harmony-context-menu-item.selected>.harmony-context-menu-item-title:after{content:"✔";position:absolute;right:0}.harmony-context-menu-item>.harmony-context-menu-item-title:after{height:32px;transition:all .2s ease 0s;width:32px}.harmony-context-menu-item.closed>.harmony-context-menu-item-title,.harmony-context-menu-item.opened>.harmony-context-menu-item-title{padding-right:32px}.harmony-context-menu-item.closed>.harmony-context-menu-item-title:after{content:"➤";position:absolute;right:0}.harmony-context-menu-item.opened>.harmony-context-menu-item-title:after{content:"➤";position:absolute;right:0;transform:rotate(90deg)}.harmony-context-menu-item .submenu{background-color:var(--theme-context-menu-submenu-bg-color);background-color:var(--theme-context-menu-submenu-fg-color);display:none;margin-left:2px;overflow:hidden;padding-left:10px;position:relative}.harmony-context-menu-item.opened>.submenu{display:block}`);
